<?php
// Database connection
$conn = new mysqli('localhost', 'root', '', 'scholarship_registration');

// Check connection
if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// Check if the form is submitted
if ($_SERVER["REQUEST_METHOD"] == "POST") {
    // Retrieve form data
    $userID = $_POST['userID'];
    $password = $_POST['password'];
    $studentName = $_POST['studentName'];
    $stateOfOrigin = $_POST['stateOfOrigin'];
    $contactEmail = $_POST['contactEmail'];
    $contactPhone = $_POST['contactPhone'];
    $aadharNumber = $_POST['aadharNumber'];
    $incomeCertificateNumber = $_POST['incomeCertificateNumber'];
    $residentialCertificateNumber = $_POST['residentialCertificateNumber'];
    $collegeName = $_POST['collegeName'];
    $collegeLocation = $_POST['collegeLocation'];
    $collegeType = $_POST['collegeType'];
    $collegeID = $_POST['collegeID'];
    $currentYear = $_POST['currentYear'];
    $course = $_POST['course'];
    $registrationNumber = $_POST['registrationNumber'];
    $familyIncome = $_POST['familyIncome'];
    $bankAccountNumber = $_POST['bankAccountNumber'];
    $bankBranch = $_POST['bankBranch'];

    // Insert data into the 'students' table
    $sql = "INSERT INTO students (userID, password, studentName, stateOfOrigin, contactEmail, contactPhone, aadharNumber, 
            incomeCertificateNumber, residentialCertificateNumber, collegeName, collegeLocation, collegeType, collegeID, 
            currentYear, course, registrationNumber, familyIncome, bankAccountNumber, bankBranch)
            VALUES ('$userID', '$password', '$studentName', '$stateOfOrigin', '$contactEmail', '$contactPhone', 
            '$aadharNumber', '$incomeCertificateNumber', '$residentialCertificateNumber', '$collegeName', '$collegeLocation', 
            '$collegeType', '$collegeID', '$currentYear', '$course', '$registrationNumber', '$familyIncome', 
            '$bankAccountNumber', '$bankBranch')";

    if ($conn->query($sql) === TRUE) {
        // Registration successful
        echo "<bold><center style=\"padding:40px;color:white;font-size:30px;\">Registration successful...!</center><br></bold>";
        echo "<center><button style=\"width:100px; height:70px; font-size:18px;\"><a  href=\"dash.html\">Go To Dashboard</a></button></center>";
    } else {
        echo "Error inserting student details: " . $conn->error;
    }

    // Close the database connection
    $conn->close();
}
?>


</body>
</html>




